export const analyzeDiseaseImages = async (images, crop, symptoms, language = 'en') => {
    // Hardcoding as a fallback to ensure it works even if .env is not picked up
    const apiKey = 'AIzaSyArwjyiNOVSQvX-jC4XXbjDq4LEWkEQ5wI';

    const prompt = `
        Identify the disease in this ${crop} plant.
        User symptoms: ${symptoms || 'None'}.
        Provide a JSON response with these keys: disease, confidence, symptoms (array), treatments (array), preventiveMeasures (array).
        Reply ONLY with valid JSON.
    `;

    try {
        const imageParts = images.map(img => {
            const base64Data = img.split(',')[1];
            const mimeType = img.split(',')[0].split(':')[1].split(';')[0];
            return {
                inline_data: {
                    data: base64Data,
                    mime_type: mimeType
                }
            };
        });

        // Try primary model
        let response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash:generateContent?key=${apiKey}`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                contents: [{
                    parts: [
                        { text: prompt },
                        ...imageParts
                    ]
                }]
            })
        });

        // If primary fails, try fallback 1 (latest)
        if (!response.ok) {
            console.warn('Primary model failed, trying fallback 1 (latest)...');
            response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=${apiKey}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    contents: [{
                        parts: [
                            { text: prompt },
                            ...imageParts
                        ]
                    }]
                })
            });
        }

        // If fallback 1 fails, try fallback 2 (8b)
        if (!response.ok) {
            console.warn('Fallback 1 failed, trying fallback 2 (8b)...');
            response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-8b:generateContent?key=${apiKey}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    contents: [{
                        parts: [
                            { text: prompt },
                            ...imageParts
                        ]
                    }]
                })
            });
        }

        // Final check for response success
        if (!response.ok) {
            const errorData = await response.json().catch(() => ({}));
            throw new Error(errorData.error?.message || `API request failed with status ${response.status}`);
        }

        const data = await response.json();

        if (data.error) {
            throw new Error(data.error.message);
        }

        if (!data.candidates || !data.candidates[0] || !data.candidates[0].content) {
            throw new Error('No analysis result was returned by the AI.');
        }

        const text = data.candidates[0].content.parts[0].text;
        // Basic JSON extraction in case the model returns markdown code blocks
        const jsonMatch = text.match(/\{[\s\S]*\}/);
        const result = JSON.parse(jsonMatch ? jsonMatch[0] : text);

        return result;
    } catch (error) {
        console.error('Gemini Analysis Error:', error);
        throw error;
    }
};
