const translations = {
    en: {
        disease_detection_nav: 'Disease Detection',
        disease_detection_subtitle: 'Upload images of your plants to detect diseases and get treatment recommendations',
        which_crop: 'Which crop are you growing?',
        upload_plant_images: 'Upload Plant Images',
        upload_limit_subtitle: 'Upload up to 5 images showing plant symptoms',
        upload_click: 'Click to upload image',
        upload_limit: 'PNG, JPG up to 10MB',
        symptoms_observed: 'Symptoms Observed (Optional)',
        symptoms_placeholder: 'e.g., Yellow spots on leaves, wilting, brown edges...',
        analyze_images: 'Analyze Images',
        disease_analysis: 'Disease Analysis',
        confidence: 'Confidence',
        treatment_recommendations: 'Treatment Recommendations',
        offline_mode_msg: 'You are currently offline. Analysis requires an internet connection.',
        new_analysis: 'New Analysis'
    },
    hi: {
        disease_detection_nav: 'पौधों के रोग की पहचान',
        which_crop: 'आप कौन सी फसल उगा रहे हैं?',
        upload_plant_images: 'पौधे की तस्वीरें अपलोड करें',
        upload_click: 'यहाँ क्लिक करें या तस्वीरें खींचें',
        upload_limit: 'अधिकतम 5 तस्वीरें, प्रत्येक 5MB तक',
        symptoms_observed: 'देखे गए लक्षण (वैकल्पिक)',
        analyze_images: 'तस्वीरों का विश्लेषण करें',
        disease_analysis: 'रोग विश्लेषण',
        confidence: 'विश्वास स्तर',
        treatment_recommendations: 'उपचार के सुझाव',
        offline_mode_msg: 'आप अभी ऑफलाइन हैं। विश्लेषण के लिए इंटरनेट कनेक्शन की आवश्यकता है।',
        new_analysis: 'नया विश्लेषण'
    }
};

export const useTranslation = (lang = 'en') => {
    const t = (key) => {
        return translations[lang]?.[key] || translations['en'][key] || key;
    };
    return { t };
};
