import React, { useState } from 'react';
import { PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import styles from '../styles/YieldPrediction.module.css';

const YieldPrediction = () => {
    const [crop, setCrop] = useState('');
    const [area, setArea] = useState('');
    const [result, setResult] = useState(null);

    const calculate = (e) => {
        e.preventDefault();
        if (!crop || !area) return;

        // Mock Logic
        const yieldPerAcre = 20; // Quintals
        const pricePerQuintal = 2200;
        const costPerAcre = 15000;

        const totalYield = area * yieldPerAcre;
        const revenue = totalYield * pricePerQuintal;
        const totalCost = area * costPerAcre;
        const profit = revenue - totalCost;

        setResult({
            yield: totalYield,
            revenue,
            cost: totalCost,
            profit,
            breakdown: [
                { name: 'Seeds', value: totalCost * 0.15 },
                { name: 'Fertilizer', value: totalCost * 0.25 },
                { name: 'Labor', value: totalCost * 0.35 },
                { name: 'Irrigation', value: totalCost * 0.15 },
                { name: 'Pest Control', value: totalCost * 0.10 },
            ]
        });
    };

    const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8'];

    return (
        <div className="container">
            <h2 className="text-center" style={{ margin: '1.5rem 0' }}>Yield & Profit Calculator</h2>

            <div className={styles.predictionContainer}>
                <div className="card">
                    <form onSubmit={calculate} className={styles.form}>
                        <div className={styles.inputGroup}>
                            <label>Select Crop</label>
                            <select value={crop} onChange={e => setCrop(e.target.value)} required>
                                <option value="">-- Select --</option>
                                <option value="wheat">Wheat</option>
                                <option value="paddy">Paddy</option>
                                <option value="cotton">Cotton</option>
                            </select>
                        </div>

                        <div className={styles.inputGroup}>
                            <label>Land Area (Acres)</label>
                            <input type="number" value={area} onChange={e => setArea(e.target.value)} placeholder="e.g. 5" required />
                        </div>

                        <button type="submit" className="btn btn-primary w-full">Calculate</button>
                    </form>
                </div>

                {result && (
                    <div className={styles.results}>
                        <div className={styles.summaryGrid}>
                            <div className={`${styles.summaryCard} ${styles.highlight}`}>
                                <label>Net Profit</label>
                                <div className={styles.value}>₹{result.profit.toLocaleString()}</div>
                            </div>
                            <div className={styles.summaryCard}>
                                <label>Est. Yield</label>
                                <div className={styles.value}>{result.yield} Q</div>
                            </div>
                            <div className={styles.summaryCard}>
                                <label>Total Cost</label>
                                <div className={styles.value}>₹{result.cost.toLocaleString()}</div>
                            </div>
                            <div className={styles.summaryCard}>
                                <label>Revenue</label>
                                <div className={styles.value}>₹{result.revenue.toLocaleString()}</div>
                            </div>
                        </div>

                        <div className="card" style={{ marginTop: '1.5rem', height: '300px' }}>
                            <h4 className="text-center">Expense Breakdown</h4>
                            <ResponsiveContainer width="100%" height="100%">
                                <PieChart>
                                    <Pie
                                        data={result.breakdown}
                                        cx="50%"
                                        cy="50%"
                                        innerRadius={60}
                                        outerRadius={80}
                                        paddingAngle={5}
                                        dataKey="value"
                                    >
                                        {result.breakdown.map((entry, index) => (
                                            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                        ))}
                                    </Pie>
                                    <Tooltip formatter={(val) => `₹${val.toLocaleString()}`} />
                                    <Legend />
                                </PieChart>
                            </ResponsiveContainer>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default YieldPrediction;
